import React, { useEffect, useState } from "react";
import { Table, Spinner, Alert } from "react-bootstrap";
import axios from "axios";

interface DataType {
  ip: string;  // Первичный ключ
  pingTime: number;
  lastSuccessful: string;  // Дата последнего успешного пинга
}

const DataTable: React.FC = () => {
  const [data, setData] = useState<DataType[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    axios.get("http://localhost:8080/v1/containers/") // Замените на свой URL
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        setError("Ошибка загрузки данных");
        console.error(error);
      })
      .finally(() => setLoading(false));
  }, []);

  return (
    <div className="container mt-4">
      <h2>Статистика пинга</h2>
      {loading && <Spinner animation="border" />}
      {error && <Alert variant="danger">{error}</Alert>}
      {!loading && !error && (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>IP-адрес</th>
              <th>Время пинга (мс)</th>
              <th>Последний успешный пинг</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item) => (
              <tr key={item.ip}>
                <td>{item.ip}</td>
                <td>{item.pingTime} мс</td>
                <td>{new Date(item.lastSuccessful).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </div>
  );
};

export default DataTable;
